import React, { useState } from 'react';
import myCat from '../../src/myCat.png'
import itemImage from '../../src/item.png'

const myCatCell = { 'isEmpty': false, "isMyCat": true }
const itemCell = { 'isEmpty': false, "isItem": true }

// let initialArrState = [
//   [null, myCatCell, itemCell, itemCell, null, null, null, null, null, null],
//   [null, null, null, null, null, null, null, null, null, null],
//   [null, null, null, null, null, itemCell, null, null, null, null],
//   [null, null, null, null, null, null, null, null, null, null],
//   [null, null, null, null, itemCell, null, null, null, null, null],
//   [null, null, null, itemCell, null, null, null, null, null, null]
// ]

let initialArrState = [
  [null, myCatCell, itemCell],
  [itemCell, null, itemCell],
  [null, null, null],
  [null, null, null],
  [null, null, null],
  [null, null, null],
]

export default function CustomField(value) {
  const [arrState, setArrState] = useState(initialArrState);

  function createItems(arrState) {
    console.log(value)
    const listItems = arrState.map((number, id) => {
      return <div className="line-item" id={id}>
        {number.map((item, itemId) => {
          return <div onClick={() => handleCellClick(id, itemId)} className="cell-item" id={itemId}>{checkCellAndRender(item)}</div>
        })}
      </div>
    });
    return listItems;
  }

  function checkCellAndRender(item) {
    if (item) {
      if (item.isMyCat) {
        return <img src={myCat} />;
      }
      if (item.isItem) {
        return <img src={itemImage} />;
      }
    } else return null;
  }

  function handleCellClick(clickRowId, clickCellId) {
    if (arrState[clickRowId][clickCellId]) {
      if (!arrState[clickRowId][clickCellId].isEmpty) {
        console.log("не пустое");
        return;
      }
    }
    cleanOldCat(arrState, myCatCell);
    updateElement(clickRowId, clickCellId, myCatCell);

  }

  function cleanOldCat(matrix, target) {
    for (let i = 0; i < matrix.length; i++) {
      const columnIndex = matrix[i].findIndex(element => element === target);
      if (columnIndex !== -1) {
        updateElement(i, columnIndex, null);
      }
    }
  }

  const updateElement = (rowIndex, columnIndex, newValue) => {
    const updatedMatrix = [...arrState];
    updatedMatrix[rowIndex][columnIndex] = newValue;
    setArrState(updatedMatrix);
  };

  return (
    <div className="custom-field">{createItems(arrState)}</div>
  );
}
