import React, { useState } from 'react';
import myCat from '../../src/myCat.png'

const myCatCell = { 'isEmpty': false, "isMyCat": true, }

let initialArrState = [
  [null, myCatCell, null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null, null, null]
]

export default function CustomField() {
  const [arrState, setArrState] = useState(initialArrState);

  function createItems(arrState) {
    const listItems = arrState.map((number, id) => {
      return <div className="line-item" id={id}>
        {number.map((item, itemId) => {
          return <div onClick={() => handleCellClick(id, itemId)} className="cell-item" id={itemId}>{checkCellAndRender(item)}</div>
        })}
      </div>
    });
    return listItems;
  }
  function checkCellAndRender(item) {
    if (item) {
      if (item.isMyCat) {
        return <img src={myCat} />;
      }
    } else return null;
  }

  function handleCellClick(id, itemId) {
    arrState.forEach((element) => {
      element.find(searchItem) && updateElement(id, itemId);
      // element.find(searchItem)

    })
  }

  function searchItem(element, index, array) {
    if (element === myCatCell) {
      // console.log(array)
      // array[index] = null;
      // setArrState(array[index])
      return index;
    } else return null;
  }

  const updateElement = (rowIndex, columnIndex, newValue) => {
    const updatedMatrix = [...matrix]; // Создаем копию исходного массива

    updatedMatrix[rowIndex] = [...updatedMatrix[rowIndex]];

    updatedMatrix[rowIndex][columnIndex] = newValue; 

    setArrState(updatedMatrix);
  };

  return (
    <div className="custom-field">{createItems(arrState)}</div>
  );
}
